import VueRouter from "vue-router";

import IndexComponent from "@/components/IndexComponent.vue";


// 定义路由数组
const vueRouter = new VueRouter( {
  routes:[
    {
      path: '/order-confirmation', // 订单确认页面的路由路径

      component: IndexComponent// 指定组件
    }
  ]
})

// 可以根据需要添加其他路由


// 创建路由器实例

export default vueRouter; // 导出 router 实例
