// main.js
import { createApp } from 'vue';
import App from '@/App.vue';
import '@fortawesome/fontawesome-free/css/all.css';
import router from '@/router/index.js'; // 确保路由文件已更新为 Vue Router 4

// 创建 Vue 应用实例
const app = createApp(App);

// 使用 Vue Router
app.use(router);

// 挂载应用实例
app.mount('#app');
