import "core-js/modules/es.array.push.js";
import { ref, onMounted, computed } from 'vue';
import { useRouter } from 'vue-router';
import { orderApi } from '../utils/api';
import { businessApi } from '../utils/api'; // Added import for businessApi
import { ElMessage } from 'element-plus'; // Added ElMessage import

const baseURL = 'http://localhost:8090'; // API网关地址

export default {
  __name: 'historyComponent',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const router = useRouter();
    const orders = ref([]);
    const activeTab = ref('all');
    onMounted(() => {
      fetchOrders();
    });
    const goBack = () => {
      router.push('/Index-Component');
    };
    const setActiveTab = tab => {
      activeTab.value = tab;
      // 切换标签时滚动到顶部
      window.scrollTo(0, 0);
    };

    // 将逗号分隔的字符串转换为数组
    const getArrayFromString = str => {
      if (!str) return [];
      try {
        // 检查是否是单个Base64编码字符串（通常很长）
        if (typeof str === 'string' && str.length > 100) {
          // 如果是Base64编码或包含Base64特征，作为单个元素返回
          if (str.startsWith('data:image') || str.startsWith('iVBOR') || /^[A-Za-z0-9+/=]+$/.test(str.substring(0, 20))) {
            return [str];
          }
        }

        // 如果字符串包含多个Base64编码（可能性较低，但为了完整性）
        if (typeof str === 'string' && (str.includes('data:image') || str.includes('iVBOR'))) {
          // 尝试在特定模式下分割，而不是简单地在逗号处分割
          const parts = [];
          let currentPart = '';
          let inBase64 = false;
          for (let i = 0; i < str.length; i++) {
            if (str.substring(i, i + 10) === 'data:image' || str.substring(i, i + 5) === 'iVBOR') {
              if (currentPart && !inBase64) {
                parts.push(currentPart.trim());
                currentPart = '';
              }
              inBase64 = true;
            }
            if (str[i] === ',' && !inBase64) {
              parts.push(currentPart.trim());
              currentPart = '';
              continue;
            }
            currentPart += str[i];

            // 检测Base64结束
            if (inBase64 && i > 100 && str[i] === '=' && (str[i - 1] === '=' || str[i + 1] === ',')) {
              inBase64 = false;
              parts.push(currentPart.trim());
              currentPart = '';
              i++; // 跳过可能的逗号
            }
          }
          if (currentPart) {
            parts.push(currentPart.trim());
          }
          return parts.filter(p => p);
        }

        // 处理普通的逗号分隔字符串
        return str.split(',');
      } catch (error) {
        console.error('分割字符串出错:', error);
        return [];
      }
    };

    // 处理图片URL
    const getImageUrl = imgPath => {
      if (!imgPath) {
        return require('@/assets/img/BUSINESS/img.png');
      }
      try {
        // 如果是Base64编码的图片数据
        if (typeof imgPath === 'string' && imgPath.length > 100) {
          // 检查是否是完整的Base64字符串
          if (imgPath.startsWith('data:image')) {
            return imgPath;
          }

          // 如果是不完整的Base64字符串（没有前缀），添加前缀
          if (imgPath.startsWith('iVBOR')) {
            return 'data:image/png;base64,' + imgPath;
          }

          // 其他可能的Base64编码（JPEG等）
          if (/^[A-Za-z0-9+/=]+$/.test(imgPath.substring(0, 20))) {
            return 'data:image/jpeg;base64,' + imgPath;
          }
        }

        // 如果是完整URL，直接返回
        if (imgPath.startsWith('http')) {
          return imgPath;
        }

        // 如果是以/assets开头的路径，转换为正确的路径
        if (imgPath.startsWith('/assets/')) {
          // 去掉开头的/assets/，因为在项目中assets是相对路径
          const relativePath = imgPath.replace('/assets/', '');
          try {
            return require(`@/assets/${relativePath}`);
          } catch (e) {
            // 尝试通过API网关获取
            return baseURL + imgPath;
          }
        }

        // 如果是相对路径，添加baseURL
        if (imgPath.startsWith('/')) {
          return baseURL + imgPath;
        }

        // 尝试加载项目中的图片
        try {
          // 首先尝试从BUSINESS目录加载
          if (!imgPath.includes('/')) {
            try {
              return require('@/assets/img/BUSINESS/' + imgPath);
            } catch (e) {
              // 尝试从其他目录加载
            }
          }

          // 然后尝试从普通img目录加载
          return require('@/assets/img/' + imgPath);
        } catch (e) {
          return require('@/assets/img/BUSINESS/img.png');
        }
      } catch (error) {
        console.error('处理图片URL出错:', error);
        return require('@/assets/img/BUSINESS/img.png');
      }
    };

    // 处理图片加载错误
    const handleImageError = (e, type) => {
      if (type === 'business') {
        e.target.src = require('@/assets/img/BUSINESS/img.png');
      } else {
        e.target.src = require('@/assets/img/sp01.png');
      }
    };

    // 再次下单
    const reorder = order => {
      // 实现再次下单功能
      router.push(`/business-component/${order.businessId}`);
    };

    // 前往评价页面
    const goToRating = order => {
      console.log('Order object:', order);

      // 确保businessId存在且为有效值
      const businessId = order.businessId || order.business?.businessId || '1';

      // 如果商家图片为空，尝试再次获取
      if (!order.businessImg && businessId) {
        businessApi.getBusinessById(businessId).then(businessInfo => {
          if (businessInfo && businessInfo.businessImg) {
            navigateToRating(order, businessInfo.businessImg);
          } else {
            navigateToRating(order);
          }
        }).catch(error => {
          console.error('获取商家信息失败:', error);
          navigateToRating(order);
        });
      } else {
        navigateToRating(order);
      }
    };

    // 导航到评价页面
    const navigateToRating = (order, businessImg = null) => {
      router.push({
        name: 'Rating',
        params: {
          orderId: order.orderId || '1',
          businessId: order.businessId || order.business?.businessId || '1',
          businessName: order.businessName || '未知商家'
        },
        query: {
          businessImg: businessImg || order.businessImg || '',
          orderDate: order.orderDate || ''
        }
      });
    };

    // 检查订单是否已评价
    const isOrderRated = orderId => {
      try {
        // 检查本地存储中是否有该订单的评价
        const ratingsString = localStorage.getItem('localRatings');
        console.log(`检查订单 ${orderId} 是否已评价, 本地存储:`, ratingsString);
        if (ratingsString) {
          const ratings = JSON.parse(ratingsString);
          console.log('解析后的本地评价:', ratings);

          // 确保使用数字类型进行比较
          const isRated = ratings.some(r => r.orderId === Number(orderId));
          console.log(`订单 ${orderId} 已评价状态:`, isRated);
          return isRated;
        }
      } catch (error) {
        console.error('检查订单评价状态失败:', error);
      }
      return false;
    };
    const fetchOrders = async () => {
      try {
        const response = await orderApi.getOrderHistory();
        console.log('订单历史原始响应:', response);
        if (response && Array.isArray(response)) {
          orders.value = response;
        } else if (response && response.data && Array.isArray(response.data)) {
          orders.value = response.data;
        } else if (response && typeof response === 'object') {
          // 尝试从响应中提取数据
          console.log('尝试从响应中提取订单数据:', response);
          const possibleOrderArrays = [response.data, response.result, response.content, response.records, response.list, response.orders];
          for (const arr of possibleOrderArrays) {
            if (arr && Array.isArray(arr)) {
              console.log('找到订单数组:', arr);
              orders.value = arr;
              break;
            }
          }
        }

        // 详细记录每个订单的图片信息
        console.log('订单数据处理前:', JSON.stringify(orders.value.map(order => ({
          orderId: order.orderId,
          businessImg: order.businessImg ? order.businessImg.substring(0, 30) + '...' : 'null',
          businessImgType: order.businessImg ? typeof order.businessImg : 'null',
          hasBusinessImg: !!order.businessImg,
          foodImgs: order.foodImgs ? order.foodImgs.substring(0, 30) + '...' : 'null',
          // 添加更多详细的食品图片信息
          foodImgsArray: getArrayFromString(order.foodImgs),
          foodImgsFirstItem: getArrayFromString(order.foodImgs)[0] ? getArrayFromString(order.foodImgs)[0].substring(0, 30) + '...' : 'null'
        }))));

        // 确保每个订单对象都有必要的属性
        orders.value = orders.value.map(order => {
          // 使用数据库中存储的商家名称和图片
          const businessName = order.businessName || order.business?.businessName || '未知商家';
          const businessImg = order.businessImg || order.business?.businessImg || '';
          console.log(`订单 ${order.orderId} 的商家图片:`, businessImg ? businessImg.substring(0, 30) + '...' : 'null', '类型:', typeof businessImg);

          // 检查是否有订单详情中的食品图片
          if (order.orderDetailList && Array.isArray(order.orderDetailList)) {
            console.log(`订单 ${order.orderId} 的食品图片:`, order.orderDetailList.map(detail => ({
              foodId: detail.foodId,
              foodImg: detail.foodImg ? detail.foodImg.substring(0, 30) + '...' : 'null'
            })));
          }

          // 打印foodImgs字段的详细信息
          if (order.foodImgs) {
            console.log(`订单 ${order.orderId} 的foodImgs字段:`, order.foodImgs);
            const foodImgsArray = getArrayFromString(order.foodImgs);
            console.log(`订单 ${order.orderId} 的foodImgs数组:`, foodImgsArray);

            // 检查每个食品图片URL
            foodImgsArray.forEach((imgUrl, index) => {
              console.log(`食品图片 ${index}:`, imgUrl);
              console.log(`处理后的图片URL:`, getImageUrl(imgUrl));
            });
          }
          return {
            ...order,
            businessId: order.businessId || order.business?.businessId || '1',
            businessName: businessName,
            businessImg: businessImg,
            orderDate: order.orderDate || new Date(order.orderTime || Date.now()).toLocaleString()
          };
        });
        console.log('处理后的订单数据:', orders.value);

        // 不再需要为每个订单获取商家信息，因为数据库中已经存储了商家名称和图片
      } catch (error) {
        console.error('获取订单历史失败:', error);

        // 如果API调用失败，尝试从本地存储获取订单信息
        const localOrderInfo = localStorage.getItem('orderInfo');
        if (localOrderInfo) {
          try {
            const orderInfo = JSON.parse(localOrderInfo);
            console.log('使用本地存储的订单信息:', orderInfo);

            // 创建模拟订单数据
            const mockOrder = {
              orderId: new Date().getTime(),
              businessId: orderInfo.businessId,
              businessName: orderInfo.businessName,
              businessImg: orderInfo.businessImg,
              orderDate: orderInfo.orderDate || new Date().toLocaleString(),
              orderTotal: orderInfo.orderTotal || 0,
              orderState: 5,
              // 已完成/待评价
              foodNames: orderInfo.items?.map(item => item.foodName).join(','),
              foodImgs: orderInfo.items?.map(item => item.foodImg).join(','),
              quantities: orderInfo.items?.map(item => item.quantity).join(',')
            };

            // 使用模拟数据
            orders.value = [mockOrder];

            // 显示提示
            ElMessage({
              message: 'API服务不可用，显示本地存储的订单信息',
              type: 'warning',
              duration: 3000
            });
          } catch (e) {
            console.error('解析本地订单信息失败:', e);

            // 使用硬编码的模拟数据
            useMockData();
          }
        } else {
          // 如果本地存储中没有订单信息，使用硬编码的模拟数据
          useMockData();
        }
      }
    };

    // 使用硬编码的模拟数据
    const useMockData = () => {
      console.log('使用硬编码的模拟订单数据');

      // 创建模拟订单数据
      const mockOrders = [{
        orderId: 1,
        businessId: 1,
        businessName: '万家饺子（软件园E18店）',
        businessImg: '/assets/img/BUSINESS/img.png',
        orderDate: new Date().toLocaleString(),
        orderTotal: 31,
        orderState: 5,
        foodNames: '纯肉鲜肉,玉米鲜肉',
        foodImgs: '/assets/img/sp01.png,/assets/img/sp01.png',
        quantities: '1,1'
      }, {
        orderId: 2,
        businessId: 2,
        businessName: '肯德基麦当劳',
        businessImg: '/assets/img/BUSINESS/img.png',
        orderDate: new Date(Date.now() - 86400000).toLocaleString(),
        // 昨天
        orderTotal: 45,
        orderState: 5,
        foodNames: '汉堡,薯条,可乐',
        foodImgs: '/assets/img/sp01.png,/assets/img/sp01.png,/assets/img/sp01.png',
        quantities: '1,1,1'
      }];

      // 使用模拟数据
      orders.value = mockOrders;

      // 显示提示
      ElMessage({
        message: 'API服务不可用，显示模拟订单数据',
        type: 'warning',
        duration: 3000
      });
    };

    // 根据选中的标签筛选订单
    const filteredOrders = computed(() => {
      if (activeTab.value === 'all') {
        return orders.value;
      } else if (activeTab.value === 'inProgress') {
        // 订单状态：1-已下单/待付款，2-已付款/待接单，3-已接单/配送中
        return orders.value.filter(order => [1, 2, 3].includes(order.orderState));
      } else if (activeTab.value === 'toRate') {
        // 订单状态：5-已完成/待评价
        return orders.value.filter(order => order.orderState === 5);
      } else if (activeTab.value === 'refund') {
        // 订单状态：6-已退款
        return orders.value.filter(order => order.orderState === 6);
      }
      return orders.value;
    });

    // 获取食品图片的辅助函数
    const getFoodImage = (order, index) => {
      try {
        // 获取食品图片数组
        const foodImgs = getArrayFromString(order.foodImgs);

        // 检查索引是否有效
        if (index >= 0 && index < foodImgs.length) {
          return getImageUrl(foodImgs[index]);
        }

        // 如果索引无效，返回默认图片
        return require('@/assets/img/sp01.png');
      } catch (error) {
        console.error('获取食品图片出错:', error);
        return require('@/assets/img/sp01.png');
      }
    };

    // 获取食品数量的辅助函数
    const getFoodQuantity = (order, index) => {
      try {
        // 获取数量数组
        const quantities = getArrayFromString(order.quantities);

        // 检查索引是否有效
        if (index >= 0 && index < quantities.length) {
          return quantities[index] || '1';
        }

        // 如果索引无效，返回默认值
        return '1';
      } catch (error) {
        console.error('获取食品数量出错:', error);
        return '1';
      }
    };
    const __returned__ = {
      router,
      orders,
      activeTab,
      baseURL,
      goBack,
      setActiveTab,
      getArrayFromString,
      getImageUrl,
      handleImageError,
      reorder,
      goToRating,
      navigateToRating,
      isOrderRated,
      fetchOrders,
      useMockData,
      filteredOrders,
      getFoodImage,
      getFoodQuantity,
      ref,
      onMounted,
      computed,
      get useRouter() {
        return useRouter;
      },
      get orderApi() {
        return orderApi;
      },
      get businessApi() {
        return businessApi;
      },
      get ElMessage() {
        return ElMessage;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
