import { createRouter, createWebHistory } from 'vue-router'; // 导入正确的函数
import Index from "@/components/IndexComponent.vue"; // 导入首页组件
import OrderConfirmation from "@/components/OrderConfirmation.vue"; // 导入订单确认页面组件

// 定义路由数组
const routes = [
  {
    path: '/',
    name: 'Index',
    component: Index,
  },
  {
    path: '/order-confirmation', // 订单确认页面的路由路径
    name: 'OrderConfirmation',
    component: OrderConfirmation, // 指定组件
  },
  // 可以根据需要添加其他路由
];

// 创建路由器实例
const router = createRouter({
  history: createWebHistory(), // 使用 createWebHistory 创建路由历史记录
  routes, // 传入路由数组
});

export default router; // 导出 router 实例
